import React, { useState } from "react";

function TurnOff() {
  // Create a state variable to track the state of the device (on/off)
  const [isDeviceOn, setDeviceOn] = useState(false);

  // Function to toggle the device on/off
  const toggleDevice = () => {
    setDeviceOn(!isDeviceOn); // Toggle the state
  };

  return (
    <>
      <div style={{ width: "100%", height: "100%", position: "relative" }}>
        <img
          style={{
            width: 916,
            height: 578,
            left: 0,
            top: 0,
            position: "absolute",
            borderRadius: 20,
            border: "1px black solid",
          }}
          src="https://img.rawpixel.com/s3fs-private/rawpixel_images/website_content/rm428-0019-x.jpg?w=800&dpr=1&fit=default&crop=default&q=65&vib=3&con=3&usm=15&bg=F4F4F3&ixlib=js-2.2.1&s=16c4ed18afc4ccfb86f468a829d23a1d"
          alt="Loading..."
        />
        <img
          style={{
            width: 124,
            height: 124,
            left: 396,
            top: 227,
            position: "absolute",
          }}
          src={
            isDeviceOn
              ? "https://cdn-icons-png.flaticon.com/512/92/92960.png"
              : "https://cdn-icons-png.flaticon.com/512/92/92961.png"
          }
          alt={isDeviceOn ? "Turn Off" : "Turn On"}
          onClick={toggleDevice} // Add onClick event to toggle the device
          style={{ cursor: "pointer" }} // Add cursor pointer to indicate it's clickable
        />
      </div>
    </>
  );
}

export default TurnOff;
