import { useState } from "react";
import produce from "immer";

const Notes = (props) => props.data.map((note) => <div>{note.text}</div>);

// eslint-disable-next-line import/no-anonymous-default-export
function Note() {
  const initialData = [{ text: "" }, { text: "" }];
  const [data, setData] = useState(initialData);

  const handleClick = () => {
    const text = document.querySelector("#noteinput").value.trim();
    if (text) {
      const nextState = produce(data, (draftState) => {
        draftState.push({ text });
      });
      document.querySelector("#noteinput").value = "";
      setData(nextState);
    }
  };

  return (
    <>
      <div
        style={{
          width: 916,
          height: 578,
          left: 0,
          top: 0,
          position: "absolute",
          justifyContent: "center",
          alignItems: "center",
          borderRadius: 20,
          border: "1px black solid",
          backgroundImage:
            'url("https://img.rawpixel.com/s3fs-private/rawpixel_images/website_content/rm428-0019-x.jpg?w=800&dpr=1&fit=default&crop=default&q=65&vib=3&con=3&usm=15&bg=F4F4F3&ixlib=js-2.2.1&s=16c4ed18afc4ccfb86f468a829d23a1d")',
        }}
      >
        <>
          <br></br>
          <input
            id="noteinput"
            style={{ width: 650, left: 20, top: 100 }}
            type="text"
            placeholder="Enter a new note"
          />
          <button onClick={() => handleClick()}>Add note</button>
          <Notes data={data} />
        </>
      </div>
    </>
  );
}

export default Note;
