import React, { Component } from "react";
import { connect } from "react-redux";
import { addReminder, deleteReminder, deleteAllReminders } from "../Actions";
import moment from "moment";

class Reminder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "",
      dueDate: "",
    };
  }

  addReminder() {
    this.props.addReminder(this.state.text, this.state.dueDate);
    this.setState({ text: "", dueDate: "" });
  }

  deleteReminder(id) {
    this.props.deleteReminder(id);
  }

  deleteAllReminders() {
    this.props.deleteAllReminders();
  }

  renderClearButton() {
    const { reminders } = this.props;
    if (reminders.length !== 0) {
      return (
        <button
          className="btn btn-danger"
          onClick={() => this.deleteAllReminders()}
        >
          Clear Reminders
        </button>
      );
    } else {
      return (
        <button className="btn btn-danger disabled">Clear Reminders</button>
      );
    }
  }

  renderReminders() {
    const { reminders } = this.props;
    return (
      <ul className="list-group col-md-4 col-sm-9 col-xs-10">
        {reminders.map((reminder) => {
          return (
            <li key={reminder.id} className="list-group-item">
              <div className="list-item">
                <div>{reminder.text}</div>
                <div>
                  <em>{moment(new Date(reminder.dueDate)).fromNow()}</em>
                </div>
              </div>
              <div
                className="list-item delete-button"
                onClick={() => this.deleteReminder(reminder.id)}
              >
                &#x2715;
              </div>
            </li>
          );
        })}
      </ul>
    );
  }

  render() {
    return (
      <div className="App">
        <div className="title">Reminder App</div>
        <div className="form-inline reminder-form">
          <div className="form-group">
            <input
              className="form-control"
              placeholder="I have to..."
              value={this.state.text}
              onChange={(event) => this.setState({ text: event.target.value })}
            />
            <input
              className="form-control"
              type="datetime-local"
              value={this.state.dueDate}
              onChange={(event) =>
                this.setState({ dueDate: event.target.value })
              }
            />
            <button
              className="btn btn-success"
              onClick={() => this.addReminder()}
            >
              Add Reminder
            </button>
          </div>
        </div>
        {this.renderReminders()}
        {this.renderClearButton()}
      </div>
    );
  }
}

// function mapDispatchToProps(dispatch) {
//   return bindActionCreators({addReminder}, dispatch);
// }

function mapStateToProps(state) {
  return {
    reminders: state,
  };
}

export default connect(mapStateToProps, {
  addReminder,
  deleteReminder,
  deleteAllReminders,
})(Reminder);
